// src/context/AuthContext.js
import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null); // Track logged-in user
  const [token, setToken] = useState(localStorage.getItem('token') || ''); // Store JWT token
  const navigate = useNavigate();

  // Fetch user profile if a token is present
  useEffect(() => {
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      fetchProfile();
    } else {
      setUser(null);
    }
  }, [token]);

  // Login function
  const login = async (email, password, redirectPath = '/') => {
    try {
      const response = await axios.post('http://localhost:3001/api/login', { email, password });
      const { token, user } = response.data; // Expect user and token from backend
      setToken(token);
      localStorage.setItem('token', token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      setUser(user);
      navigate(redirectPath); // Redirect after successful login
      return true;
    } catch (error) {
      console.error('Login error:', error.response?.data || error.message);
      return false;
    }
  };

  // Logout function
  const logout = () => {
    setToken('');
    setUser(null);
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    navigate('/');
  };

  // Fetch user profile
  const fetchProfile = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/profile');
      setUser(response.data); // Update user state with profile data
    } catch (error) {
      console.error('Profile fetch error:', error.response?.data || error.message);
      setUser(null); // Reset user state if error occurs
    }
  };

  return (
    <AuthContext.Provider value={{ user, token, login, logout, fetchProfile }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => React.useContext(AuthContext);
